前置左旋右旋插入算法删除算法 <pre><code>private static final boolean RED = true;
private static final boolean BLACK = false;

private class Node {
Key key;//键
Value val;//相关联的值
Node left, right;//左右子树
int N;//这棵树中的结点总数
boolean color;//由其父结点指向它的链接的颜色

Node(Key key, Value val, int N, boolean color){
this.key = key;
this.val = val;
this.N = N;
this.color = color;
}
}

private boolean isRed(Node x){
if(x==null)return false;
return x.color == RED;
}
</code></pre> <pre><code>Node rotateLeft(Node h){
Node x = h.right;
h.right = x.left;
x.left = h;
x.color = h.color;
h.color = RED;
x.N = h.N;
h.N = 1 + size(h.left) + size(h.right);
return x;
}
</code></pre> <pre><code>Node rotateRight(Node h){
Node x = h.left;
h.left = x.right;
x.right = h;
x.color = h.color;
h.color = RED;
x.N = h.N;
h.N = 1 + size(h.left) + size(h.right);
return x;
}
